independent-def:sqlext_object.independent-def:sql:
SELECT name as user_name
		FROM sys.sql_logins 
		WHERE type_desc = 'SQL_LOGIN' AND is_disabled = 0;

EXECUTE AS LOGIN = 'NT AUTHORITY\SYSTEM';
SELECT * FROM fn_my_permissions(NULL, 'server')
WHERE permission_name NOT IN ('CONNECT SQL', 'VIEW SERVER STATE', 'VIEW ANY DATABASE');
REVERT;

EXECUTE AS LOGIN = 'NT AUTHORITY\SYSTEM';
SELECT * FROM fn_my_permissions(NULL, 'server')
WHERE permission_name NOT IN ('CONNECT SQL', 'CREATE AVAILABILITY GROUP', 'ALTER ANY AVAILABILITY GROUP', 'VIEW SERVER STATE', 'VIEW ANY DATABASE', 'VIEW SERVER PERFORMANCE STATE', 'VIEW SERVER SECURITY STATE');
REVERT;

EXECUTE AS LOGIN = 'NT AUTHORITY\SYSTEM';
SELECT * FROM fn_my_permissions(NULL, 'server')
WHERE permission_name NOT IN ('CONNECT SQL', 'VIEW ANY DATABASE');
REVERT;

SELECT name, value, value_in_use 
FROM sys.configurations 
WHERE name = 'replication xps';

SELECT name, value, value_in_use 
FROM sys.configurations 
WHERE name = 'external scripts enabled';

SELECT name, value, value_in_use 
FROM sys.configurations 
WHERE name = 'remote data archive';

SELECT name, value, value_in_use 
FROM sys.configurations 
WHERE name = 'allow polybase export';

SELECT name, value, value_in_use 
FROM sys.configurations 
WHERE name = 'hadoop connectivity';

